import React, { useState, useEffect } from 'react';
import axiosInstance from '../api/axios';
import Post from '../components/Post';
import CreatePost from '../components/CreatePost';
import { useAuth } from '../context/AuthContext';

const Homepage = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { currentUser } = useAuth();

  const fetchPosts = async () => {
    try {
      setLoading(true);
      const response = await axiosInstance.get('/posts/get_all_post');
      setPosts(response.data);
      setError('');
    } catch (err) {
      setError('Не удалось загрузить посты');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  const handlePostCreated = () => {
    fetchPosts(); // Обновляем посты после создания нового
  };

  const handlePostDeleted = (deletedPostId) => {
    setPosts(posts.filter(post => post.id !== deletedPostId));
  };

  return (
    <div className="container mx-auto px-4 py-6 max-w-2xl">
      <h1 className="text-2xl font-bold text-dark mb-6">Лента записей</h1>
      
      <CreatePost onPostCreated={handlePostCreated} />
      
      <div className="my-8">
        {loading ? (
          <div className="flex justify-center items-center h-40">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary"></div>
          </div>
        ) : error ? (
          <div className="text-center text-red-500 py-4">
            <p>{error}</p>
            <button 
              onClick={fetchPosts}
              className="mt-2 text-primary hover:underline"
            >
              Попробовать снова
            </button>
          </div>
        ) : posts.length > 0 ? (
          <div>
            {posts.map(post => (
              <Post 
                key={post.id} 
                post={post} 
                onDelete={handlePostDeleted}
              />
            ))}
          </div>
        ) : (
          <div className="text-center text-secondary py-10">
            <p className="text-lg">Пока нет постов</p>
            {currentUser && (
              <p className="mt-2">Создайте первый пост!</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Homepage;
