import React, { useState } from 'react';
import axiosInstance from '../api/axios';
import { FaPaperPlane } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';

const CreatePost = ({ onPostCreated }) => {
  const { currentUser } = useAuth();
  const [formData, setFormData] = useState({
    title: '',
    short_description: '',
    description: '',
    time: Math.floor(Date.now() / 1000)
  });
  const [isExpanded, setIsExpanded] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const expandForm = () => {
    if (!currentUser) {
      setError('Войдите, чтобы создать пост');
      return;
    }
    setIsExpanded(true);
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!currentUser) {
      setError('Войдите, чтобы создать пост');
      return;
    }

    if (!formData.title.trim() || !formData.short_description.trim() || !formData.description.trim()) {
      setError('Пожалуйста, заполните все поля');
      return;
    }

    setIsSubmitting(true);
    setError('');

    try {
      const response = await axiosInstance.post('/posts/create_post', {
        ...formData,
        user_id: currentUser.id
      });

      if (response.data === 'Пост успешно добавлен') {
        setFormData({
          title: '',
          short_description: '',
          description: '',
          time: Math.floor(Date.now() / 1000)
        });
        setIsExpanded(false);
        
        // Уведомляем родительский компонент о создании поста
        if (onPostCreated) {
          onPostCreated();
        }
      }
    } catch (err) {
      setError(err.response?.data?.detail || 'Произошла ошибка при создании поста');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="card">
      {error && <p className="text-red-500 mb-2 text-center">{error}</p>}

      <form onSubmit={handleSubmit}>
        {!isExpanded ? (
          <div 
            onClick={expandForm} 
            className="border border-extralight rounded-lg p-3 cursor-text hover:bg-extralight transition"
          >
            <p className="text-secondary">Что у вас нового?</p>
          </div>
        ) : (
          <>
            <div className="mb-3">
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                placeholder="Заголовок"
                className="w-full border border-extralight rounded-lg p-3 focus:outline-none focus:border-primary"
                maxLength={100}
              />
            </div>
            
            <div className="mb-3">
              <input
                type="text"
                name="short_description"
                value={formData.short_description}
                onChange={handleInputChange}
                placeholder="Краткое описание"
                className="w-full border border-extralight rounded-lg p-3 focus:outline-none focus:border-primary"
                maxLength={150}
              />
            </div>
            
            <div className="mb-3">
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                placeholder="Полное описание"
                className="w-full border border-extralight rounded-lg p-3 focus:outline-none focus:border-primary min-h-[120px]"
                maxLength={1000}
              />
            </div>
            
            <div className="flex justify-between items-center">
              <button
                type="button"
                onClick={() => setIsExpanded(false)}
                className="text-secondary hover:text-dark"
              >
                Отмена
              </button>
              
              <button
                type="submit"
                className="btn-primary flex items-center"
                disabled={isSubmitting}
              >
                {isSubmitting ? (
                  'Публикация...'
                ) : (
                  <>
                    <FaPaperPlane className="mr-2" />
                    Опубликовать
                  </>
                )}
              </button>
            </div>
          </>
        )}
      </form>
    </div>
  );
};

export default CreatePost;
