import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { FaTwitter, FaHome, FaUser, FaSignOutAlt } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';

const Navbar = () => {
  const { currentUser, logout } = useAuth();
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleLogout = async () => {
    const result = await logout();
    if (result.success) {
      navigate('/login');
    }
  };

  return (
    <nav className="bg-white border-b border-extralight sticky top-0 z-10">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center h-16">
          {/* Logo */}
          <div className="flex items-center">
            <Link to="/" className="text-primary">
              <FaTwitter className="text-2xl" />
            </Link>
          </div>

          {/* Primary Nav Items */}
          <div className="hidden md:flex items-center space-x-8">
            <Link to="/" className="flex items-center text-dark hover:text-primary transition">
              <FaHome className="mr-1" />
              <span>Главная</span>
            </Link>
            {currentUser && (
              <Link to="/profile" className="flex items-center text-dark hover:text-primary transition">
                <FaUser className="mr-1" />
                <span>Профиль</span>
              </Link>
            )}
          </div>

          {/* Right Side Nav */}
          <div className="hidden md:flex items-center space-x-4">
            {currentUser ? (
              <div className="flex items-center space-x-4">
                <span className="text-dark font-medium">{currentUser.name}</span>
                <button 
                  onClick={handleLogout}
                  className="flex items-center text-red-500 hover:bg-red-50 px-3 py-2 rounded-full"
                >
                  <FaSignOutAlt className="mr-2" />
                  Выйти
                </button>
              </div>
            ) : (
              <div className="flex items-center space-x-2">
                <Link to="/login" className="btn-outline">
                  Вход
                </Link>
                <Link to="/register" className="btn-primary">
                  Регистрация
                </Link>
              </div>
            )}
          </div>

          {/* Mobile Menu Button */}
          <div className="md:hidden">
            <button onClick={() => setIsMenuOpen(!isMenuOpen)} className="text-gray-500 hover:text-primary focus:outline-none">
              <svg className="h-6 w-6 fill-current" viewBox="0 0 24 24">
                {isMenuOpen ? (
                  <path fillRule="evenodd" d="M18.278 16.864a1 1 0 0 1-1.414 1.414l-4.829-4.828-4.828 4.828a1 1 0 0 1-1.414-1.414l4.828-4.829-4.828-4.828a1 1 0 0 1 1.414-1.414l4.829 4.828 4.828-4.828a1 1 0 1 1 1.414 1.414l-4.828 4.829 4.828 4.828z" />
                ) : (
                  <path fillRule="evenodd" d="M4 5h16a1 1 0 0 1 0 2H4a1 1 0 1 1 0-2zm0 6h16a1 1 0 0 1 0 2H4a1 1 0 0 1 0-2zm0 6h16a1 1 0 0 1 0 2H4a1 1 0 0 1 0-2z" />
                )}
              </svg>
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      {isMenuOpen && (
        <div className="md:hidden bg-white border-t border-extralight">
          <div className="container mx-auto px-4 py-3 space-y-3">
            <Link 
              to="/" 
              className="block py-2 px-4 text-dark hover:bg-extralight rounded-lg"
              onClick={() => setIsMenuOpen(false)}
            >
              <FaHome className="inline mr-2" />
              Главная
            </Link>
            
            {currentUser && (
              <Link 
                to="/profile" 
                className="block py-2 px-4 text-dark hover:bg-extralight rounded-lg"
                onClick={() => setIsMenuOpen(false)}
              >
                <FaUser className="inline mr-2" />
                Профиль
              </Link>
            )}

            {currentUser ? (
              <button 
                onClick={() => {
                  handleLogout();
                  setIsMenuOpen(false);
                }}
                className="block w-full text-left py-2 px-4 text-red-500 hover:bg-red-50 rounded-lg"
              >
                <FaSignOutAlt className="inline mr-2" />
                Выйти
              </button>
            ) : (
              <div className="flex flex-col space-y-2 pt-2 border-t border-extralight">
                <Link 
                  to="/login" 
                  className="btn-outline text-center"
                  onClick={() => setIsMenuOpen(false)}
                >
                  Вход
                </Link>
                <Link 
                  to="/register" 
                  className="btn-primary text-center"
                  onClick={() => setIsMenuOpen(false)}
                >
                  Регистрация
                </Link>
              </div>
            )}
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
