import React, { useState } from 'react';
import { FaHeart, FaRegHeart, FaComment, FaTrash } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import axiosInstance from '../api/axios';

const Post = ({ post, onDelete }) => {
  const { currentUser } = useAuth();
  const [liked, setLiked] = useState(false);
  const [commentsOpen, setCommentsOpen] = useState(false);
  const [comment, setComment] = useState('');
  const [comments, setComments] = useState([]);
  const [commentsLoaded, setCommentsLoaded] = useState(false);

  // Форматирование времени в человекочитаемый вид (пример: "2 часа назад")
  const formatTime = (timestamp) => {
    if (!timestamp) return 'Недавно';
    
    const minutes = Math.floor(timestamp / 60);
    if (minutes < 60) return `${minutes} мин. назад`;
    
    const hours = Math.floor(minutes / 60);
    if (hours < 24) return `${hours} ч. назад`;
    
    const days = Math.floor(hours / 24);
    return `${days} дн. назад`;
  };

  const handleDelete = async () => {
    try {
      await axiosInstance.delete(`/posts/delete_post_account_${post.id}`);
      onDelete(post.id);
    } catch (error) {
      console.error('Ошибка при удалении поста:', error);
    }
  };

  const loadComments = async () => {
    if (!commentsLoaded) {
      try {
        const response = await axiosInstance.get(`/comment/get_comment_post/${post.id}`);
        setComments(response.data);
        setCommentsLoaded(true);
      } catch (error) {
        console.error('Ошибка при загрузке комментариев:', error);
      }
    }
    setCommentsOpen(!commentsOpen);
  };

  const handleSubmitComment = async (e) => {
    e.preventDefault();
    if (!comment.trim()) return;

    try {
      await axiosInstance.post('/comment/create_comment', {
        description: comment,
        post_id: post.id
      });
      
      // Перезагружаем комментарии после добавления
      const response = await axiosInstance.get(`/comment/get_comment_post/${post.id}`);
      setComments(response.data);
      setComment('');
    } catch (error) {
      console.error('Ошибка при добавлении комментария:', error);
    }
  };

  return (
    <div className="card my-4">
      {/* Post Header */}
      <div className="flex justify-between items-start mb-3">
        <div className="flex items-center">
          <div className="h-10 w-10 bg-primary text-white rounded-full flex items-center justify-center font-bold">
            {post.user_name ? post.user_name.charAt(0).toUpperCase() : "U"}
          </div>
          <div className="ml-3">
            <h3 className="font-bold text-dark">
              {post.user_name || "Пользователь"}
            </h3>
            <p className="text-secondary text-sm">{formatTime(post.time)}</p>
          </div>
        </div>

        {/* Delete Post Button (for post owner only) */}
        {currentUser && post.user_id === currentUser.id && (
          <button 
            onClick={handleDelete}
            className="text-red-500 hover:bg-red-50 p-2 rounded-full"
            title="Удалить пост"
          >
            <FaTrash />
          </button>
        )}
      </div>

      {/* Post Content */}
      <Link to={`/post/${post.id}`} className="block">
        <h2 className="text-xl font-bold text-dark mb-2">{post.title}</h2>
        <p className="text-secondary mb-3">{post.short_description}</p>
      </Link>

      {/* Post Actions */}
      <div className="flex items-center mt-4 pt-3 border-t border-extralight">
        <button 
          onClick={() => setLiked(!liked)}
          className={`flex items-center mr-4 ${liked ? 'text-red-500' : 'text-secondary'} hover:text-red-500`}
        >
          {liked ? <FaHeart className="mr-1" /> : <FaRegHeart className="mr-1" />}
          <span className="text-sm">Нравится</span>
        </button>

        <button 
          onClick={loadComments}
          className="flex items-center text-secondary hover:text-primary"
        >
          <FaComment className="mr-1" />
          <span className="text-sm">Комментарии {comments.length > 0 ? `(${comments.length})` : ''}</span>
        </button>
      </div>

      {/* Comments Section */}
      {commentsOpen && (
        <div className="mt-4 pt-3 border-t border-extralight">
          {currentUser && (
            <form onSubmit={handleSubmitComment} className="mb-4">
              <div className="flex">
                <input
                  type="text"
                  value={comment}
                  onChange={(e) => setComment(e.target.value)}
                  placeholder="Написать комментарий..."
                  className="border rounded-l-lg py-2 px-3 flex-grow focus:outline-none focus:border-primary"
                />
                <button 
                  type="submit"
                  className="bg-primary text-white rounded-r-lg px-4 hover:bg-blue-600"
                >
                  Отправить
                </button>
              </div>
            </form>
          )}

          <div className="space-y-3">
            {comments.length > 0 ? (
              comments.map((comment) => (
                <div key={comment.id} className="bg-extralight bg-opacity-50 p-3 rounded-lg">
                  <div className="flex items-center mb-2">
                    <div className="h-8 w-8 bg-primary text-white rounded-full flex items-center justify-center text-sm font-bold">
                      {comment.user_name ? comment.user_name.charAt(0).toUpperCase() : "U"}
                    </div>
                    <span className="ml-2 font-bold text-sm">{comment.user_name || "Пользователь"}</span>
                  </div>
                  <p className="text-dark">{comment.description}</p>
                </div>
              ))
            ) : (
              <p className="text-center text-secondary">Нет комментариев</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Post;
